import React from 'react';


class App extends React.Component {
  state = {
    title: '',
    body: ''
  };
  render() {
    //JSX
    return(
      <div>
        <h2>Welcome to the app</h2>
        <form>
          <div className="form-input">
          <input 
          type="text"
          name="title"
          placeholder="Title"
          value={this.state.title}
          onChange={2}
          />
          </div>
          <div className="form-input">
            <textarea placeholder="body"
             name="body"
             cols="30" 
             rows="10" 
             value={this.state.body} 
             onChange={2}
             >

            </textarea>
          </div>
          <button>Button</button>
        </form>
      </div>
    );
  }
}

export default App;
